package org.python.bouncycastle.crypto.params;

import java.math.BigInteger;

public class RSAKeyParameters extends AsymmetricKeyParameter {
   private static final BigInteger ONE = BigInteger.valueOf(1L);
   private BigInteger modulus;
   private BigInteger exponent;

   public RSAKeyParameters(boolean var1, BigInteger var2, BigInteger var3) {
      super(var1);
      if (!var1 && (var3.intValue() & 1) == 0) {
         throw new IllegalArgumentException("RSA publicExponent is even");
      } else {
         this.modulus = this.validate(var2);
         this.exponent = var3;
      }
   }

   private BigInteger validate(BigInteger var1) {
      if ((var1.intValue() & 1) == 0) {
         throw new IllegalArgumentException("RSA modulus is even");
      } else if (!var1.gcd(new BigInteger("")).equals(ONE)) {
         throw new IllegalArgumentException("RSA modulus has a small prime factor");
      } else {
         return var1;
      }
   }

   public BigInteger getModulus() {
      return this.modulus;
   }

   public BigInteger getExponent() {
      return this.exponent;
   }
}
